mapper.select:

select * 
from(select rownum rnum, tour_no, tour_category_no, tour_name, tour_act_start_date, tour_act_end_date, tour_content,tour_address, tour_roadaddress,
            tour_moreinfo, tour_readcount, user_id 
            from (select * from tour order by tour_no))
where rnum >= #{ startRow } and rnum <= #{ endRow }

select *
from tour
where tour_no = #{ tour_no }

select * from tour_review
where tour_review_no = #{ tour_no } 
order by tour_review_no desc

select tour_score
from tour_review
where tour_no = #{ tour_no }

mapper.update:
	update tour 
	set tour_name = #{ tour_name }, tour_act_start_date = #{ tour_act_start_date },
			      tour_act_end_date = #{ tour_act_end_date },  tour_content = #{ tour_content }, tour_address = #{ tour_address }, tour_roadaddress = #{ tour.roadaddress },
			       #{ tour_moreinfo }, #{ user_id } 
    where tour_no = #{ tour_no }

update tour
set tour_readcount = tour_readcount + 1
where tour_no = #{ tour_no }

update tour_reivew
set tour_review_content = #{ tour_review_content }
where tour_review_no = #{ tour_review_no }
